import { Table, Badge } from 'antd';

const StatusMap = {
  running: {
    status: 'success',
    text: 'OK'
  },
  idle: {
    status: 'default',
    text: 'Idle'
  },
  processing: {
    status: 'processing',
    text: 'Processing'
  },
  stopped: {
    status: 'error',
    text: 'Stopped'
  }
};

/*
<Badge status="success" text="Success" />
<Badge status="error" text="Error" />
<Badge status="default" text="Default" />
<Badge status="processing" text="Processing" />
<Badge status="warning" text="Warning" />
*/

const getStatusBadge = (statusCode = 'running') => {
  const statusObject = StatusMap[statusCode];
  return (<Badge status={statusObject.status} text={statusObject.text} />);
};

const columns = [
  {
    title: 'Region',
    dataIndex: 'region',
    key: 'region',
    // render: text => <a>{text}</a>,
  },
  {
    title: 'Workstream',
    dataIndex: 'workstream',
    key: 'workstream',
    // render: text => <a>{text}</a>,
  },
  {
    title: 'Scheduler',
    dataIndex: 'scheduler_name',
    key: 'scheduler_name',
  },
  {
    title: 'Pipeline',
    dataIndex: 'pipeline_name',
    key: 'pipeline_name',
  },
  {
    title: 'Schedule',
    dataIndex: 'schedule',
    key: 'schedule',
  },
  {
    title: 'Status',
    dataIndex: 'status',
    key: 'status',
    render: () => getStatusBadge(),
  },
  {
    title: 'Actions',
    dataIndex: '',
    key: 'x',
    render: () => <a>Delete</a>,
  },
];


const defaultData = [
  {
    key: '1',
    workstream: 'John Brown',
    region: 'John Brown',
    scheduler_name: 32,
    pipeline_name: 32,
    address: 'New York No. 1 Lake Park',
  },
  {
    key: '2',
    workstream: 'Jim Green',
    region: 'John Brown',
    scheduler_name: 42,
    pipeline_name: 32,
    address: 'London No. 1 Lake Park',
  },
  {
    key: '3',
    workstream: 'Jim Green',
    region: 'John Brown',
    scheduler_name: 42,
    pipeline_name: 32,
    address: 'London No. 1 Lake Park',
  },
  {
    key: '4',
    workstream: 'Jim Green',
    region: 'John Brown',
    scheduler_name: 42,
    pipeline_name: 32,
    address: 'London No. 1 Lake Park',
  },
];

const PipelineList = ({ data = defaultData }) => {
  return (
    <Table columns={columns} dataSource={data} />
  );
};

export default PipelineList;
