import { useState } from 'react';
import { Badge, Button, Table } from 'antd';

const StatusMap = {
  running: {
    status: 'success',
    text: 'OK'
  },
  idle: {
    status: 'default',
    text: 'Idle'
  },
  processing: {
    status: 'processing',
    text: 'Processing'
  },
  stopped: {
    status: 'error',
    text: 'Stopped'
  },
  warning: {
    status: 'warning',
    text: 'Warning'
  }
};

const getStatusBadge = (statusCode = 'running') => {
  const statusObject = StatusMap[statusCode];
  return (<Badge status={statusObject.status} text={statusObject.text} />);
};

const columns = [
  {
    title: 'Region',
    dataIndex: 'region',
    key: 'region',
    // render: text => <a>{text}</a>,
  },
  {
    title: 'Workstream',
    dataIndex: 'workstream',
    key: 'workstream',
    // render: text => <a>{text}</a>,
  },
  {
    title: 'Scheduler',
    dataIndex: 'scheduler_name',
    key: 'scheduler_name',
  },
  {
    title: 'Pipeline',
    dataIndex: 'pipeline_name',
    key: 'pipeline_name',
  },
  {
    title: 'Schedule',
    dataIndex: 'schedule',
    key: 'schedule',
    render: () => '30 11 * * 5'
  },
  {
    title: 'Status',
    dataIndex: 'status',
    key: 'status',
    render: () => getStatusBadge(),
  },
  {
    title: 'Actions',
    dataIndex: '',
    key: 'x',
    render: () => <a>Delete</a>,
  },
];

const defaultData = [
  {
    key: '1',
    workstream: 'John Brown',
    region: 'US East (Ohio)',
    scheduler_name: 32,
    pipeline_name: 32,
    address: 'New York No. 1 Lake Park',
  },
  {
    key: '2',
    workstream: 'Jim Green',
    region: 'US East (N. Virginia)',
    scheduler_name: 42,
    pipeline_name: 32,
    address: 'London No. 1 Lake Park',
  },
  {
    key: '3',
    workstream: 'Jim Green',
    region: 'US West (N. California)',
    scheduler_name: 42,
    pipeline_name: 32,
    address: 'London No. 1 Lake Park',
  },
  {
    key: '4',
    workstream: 'Jim Green',
    region: 'US West (Oregon)',
    scheduler_name: 42,
    pipeline_name: 32,
    address: 'London No. 1 Lake Park',
  },
];

// Search by pipeline name
// Filter by Region
// Filter by Workstream 

const PipelineList = ({ data = defaultData }) => {
  const [filteredInfo, setFilteredInfo] = useState();
  // const [sortedInfo, setSortedInfo] = useState();

  const handleChange = (pagination, filters, sorter) => {
    console.log('Parameters', pagination, filters, sorter);
    setFilteredInfo(filters);
  };

  const clearFilters = () => {
    setFilteredInfo(null);
  };

  const clearAll = () => {
    setFilteredInfo(null);
  };

  return (
    <div>
      <div className="table-operations" style={{ marginBottom: '10px' }}>
        <Button onClick={clearFilters}>Clear filters</Button>
      </div>
      <Table columns={columns} dataSource={data} onChange={handleChange} />
    </div>
  );
};

export default PipelineList;
