import { useEffect, useState } from 'react';
// import Link from "next/link";
import Head from 'next/head'
import { Col, Button, Form, Input, Layout, Row, Spin } from 'antd';

import HeaderComponent from '../components/Header';

const { Content, Footer } = Layout;

const SigninForm = () => {
  const [loading, toggleLoading] = useState(false);
  const submitLoginInfo = () => {
    toggleLoading(true);
  };

  useEffect(() => {
    if (loading) setTimeout(() => toggleLoading(false), 2000);
  }, [loading]);

	return (
		<div>
      <Spin spinning={loading}>
        <Form layout="vertical">
          <Form.Item>
            <Input placeholder="Email" />
          </Form.Item>

          <Form.Item>
            <Input placeholder="Password" />
          </Form.Item>

          <Form.Item>
            <div style={{ textAlign: 'right'}}>
              <Button type="primary" onClick={submitLoginInfo}>Signin</Button>
            </div>
          </Form.Item>
        </Form>
      </Spin>
		</div>
	);
};

const Index = () => {
  return (
    <Layout className="layout">

			<Head>
        <title>Sign In</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      </Head>

      <HeaderComponent />

      <Content>
        <div style={{ background: '#FFF', paddingTop: '240px', minHeight: 720 }}>
          <Row>
            <Col span={6} offset={9}>
							<center>
                <SigninForm />
							</center>
            </Col>
          </Row>
        </div>
      </Content>

      <Footer>
        <Row>
          <Col span={16} offset={4}>
            <center><h4>Footer</h4></center>
          </Col>
        </Row>
      </Footer>
    </Layout>
  );
};

export default Index;
